                'o': function(val) {return parseInt(val, 10).toString(8);},
                's': function(val) {return val;},
                'x': function(val) {return ('' + parseInt(val, 10).toString(16)).toLowerCase();},
                'X': function(val) {return ('' + parseInt(val, 10).toString(16)).toUpperCase();}
